document.querySelector(".-lg").addEventListener("click", () => {
  localStorage.removeItem("https://yammieshoppers.com/useLocalStorage/Storage");
  window.location.assign("/");
});
document.querySelector(".-lg-o").addEventListener("click", () => {
  if (yammie) {
    localStorage.removeItem(
      "https://yammieshoppers.com/useLocalStorage/Storage"
    );
    window.location.assign("/");
  } else {
    window.location.assign("/account/login");
  }
});
window.addEventListener("DOMContentLoaded", () => {
  document.querySelectorAll("._yr").forEach((item) => {
    item.innerHTML = new Date().getFullYear();
  });
  setInterval(() => {
    document.querySelector("#comment").placeholder =
      document.querySelector("#comment").placeholder == "How do you feel?"
        ? "Any Comment..."
        : "How do you feel?";
  }, 5000);
});
