document.querySelector(".-lg").addEventListener("click", () => {
  localStorage.removeItem("https://yammieshoppers.com/useLocalStorage/Storage");
  window.location.assign("/");
});
document.querySelector(".-lg-o").addEventListener("click", () => {
  if (yammie) {
    localStorage.removeItem(
      "https://yammieshoppers.com/useLocalStorage/Storage"
    );
    window.location.assign("/");
  } else {
    window.location.assign("/account/login");
  }
});

function _load(i, d) {
  if (i.complete != true) {
    i.height = "35%";
    i.width = "35%";
  }
  i.src = i.complete != true ? d : i.src;
  i.src = i.complete != true ? d : i.src;
}
window.addEventListener("DOMContentLoaded", () => {
  document.querySelectorAll("._yr").forEach((item) => {
    item.innerHTML = new Date().getFullYear();
  });

  setInterval(() => {
    document.querySelector("#comment").placeholder =
      document.querySelector("#comment").placeholder == "How do you feel?"
        ? "Any Comment..."
        : "How do you feel?";
  }, 5000);
});

function ctg_storage(c, url) {
  let xhr = new XMLHttpRequest();
  xhr.onreadystatechange = () => {
    if (xhr.readyState == 4 && xhr.status == 200) {
      let _c = JSON.parse(xhr.responseText),
        group = "";
      _c.forEach((item) => {
        group += `
             <a class="item -ct-item" href="./item?item=${item.id}">
                      <div class="-d">
                        ${
                          item.discount !== null
                            ? "-" + item.discount + "%"
                            : ""
                        }
                      </div>
                      <div class="_image">
                        <img
                          src="${JSON.parse(item.images)[0]}"
                          alt="${item.name}"
                          width="100%"
                          height="100%"
                        />
                      </div>
                      <div class="-i-dtl">
                        <div class="it-nm">
                          ${item.product} - ${item.description}
                        </div>
                        <div class="it-pr pr price-current">${
                          "UGX " +
                          (item.discount
                            ? (item.price * (100 - item.discount)) / 100
                            : item.price)
                        }</div>
                         <div class="pre-it-pr pr price-prev">${
                           item.discount ? "UGX " + item.price : ""
                         }</div>
                      </div>
                    </a>
                    
            `;
      });
      c.innerHTML += group;
    }
  };
  xhr.open("GET", url, true);
  xhr.send();
}

function _rv() {
  if (
    localStorage.getItem("_rv") &&
    document.querySelector(".-ct-recentlyviewed")
  ) {
    let _rv = JSON.parse(localStorage.getItem("_rv"));
    document.querySelector(".-ct-recentlyviewed").innerHTML = "";
    _rv.forEach((item) => {
      ctg_storage(
        document.querySelector(".-ct-recentlyviewed"),
        `/api/user/product/ct/${item}`
      );
    });
  }
}
if (!localStorage.getItem("_rv") && document.querySelector(".rv-ctr") != null) {
  document.querySelector(".rv-ctr").style.display = "none";
}
_rv();
